var FormRepeater = function() {
    return {
        init: function() {
            $(".mt-repeater").each(function() {
                $(this).repeater({
                    show: function() {
                        $(this).slideDown();
                    },
                    hide: function(e) {
                        confirm("Tem certeza que deseja deletar?") && $(this).slideUp(e)
                    },
                    ready: function(e) {}
                })
            })
        }
    }
}();
jQuery(document).ready(function() {
    FormRepeater.init()
});